query:
{
  repository(name: "sweet-as-organics-api", owner: "kahikatea-2022") {
    refs(
      refPrefix: "refs/heads/"
      orderBy: {direction: DESC, field: TAG_COMMIT_DATE}
      first: 100
    ) {
      edges {
        node {
          ... on Ref {
            name
            target {
              ... on Commit {
                history(first: 1) {
                  edges {
                    node {
                      ... on Commit {
                        committedDate
                      }
                    }
                  }
                }
              }
            }
          }
          name
        }
      }
    }
  }
}
